import React from 'react';
import { Typography, Box } from '@mui/material';
import styled from 'styled-components';

const StyledBox = styled(Box)`
  padding: 2rem;
`;

const Title = styled.h4`
  color: #FF6347;
  margin-bottom: 2rem;
  font-size: 2rem;
  display: flex;
  align-items: center;
  position: relative;
  margin: 10px 0 40px;
  width: 100%;
  white-space: nowrap;
  font-size: clamp(26px, 5vw, 32px);
  font-weight: 600;

  &:before {
    counter-increment: section;
    content: '0' counter(section) '.';
    margin-right: 10px;
    color: #FF6347;
    font-family: 'SF Mono', 'Fira Code', 'Fira Mono', 'Roboto Mono', monospace;
    font-size: clamp(16px, 3vw, 20px);
    font-weight: 400;
    }
`;


const About = () => {
  return (
    <StyledBox id="about">
      <Title variant="h4" gutterBottom>About Me</Title>
      <Typography variant="body1">Write a brief introduction about yourself here.</Typography>
    </StyledBox>
  );
};

export default About;
