import React from 'react';
import { Card, CardMedia, CardContent, Typography, Box, CardActions, Button } from '@mui/material';
import styled from 'styled-components';

const StyledCard = styled(Card)`
  position: relative;
  &:hover .project-overlay {
    opacity: 1;
  }
`;

const ProjectOverlay = styled(Box)`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.7);
  color: white;
  opacity: 0;
  transition: opacity 0.3s;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const ProjectCard = ({ project }) => {
  return (
    <StyledCard sx={{ maxWidth: 345 }}>
      <CardMedia
        component="img"
        alt={project.title}
        height="140"
        image={project.cover}
        title={project.title}
      />
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
          {project.title}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {project.tech.join(', ')}
        </Typography>
      </CardContent>
      <ProjectOverlay className="project-overlay">
        <Typography variant="body1" align="center" paragraph>
          {project.description}
        </Typography>
        <CardActions>
          <Button size="small" color="primary" href={project.github} target="_blank">
            GitHub
          </Button>
          {project.external && (
            <Button size="small" color="primary" href={project.external} target="_blank">
              External
            </Button>
          )}
        </CardActions>
      </ProjectOverlay>
    </StyledCard>
  );
};

export default ProjectCard;
