import React from 'react';
import { Card, CardMedia, CardContent, Typography, Box, CardActions, Button } from '@mui/material';
import styled from 'styled-components';
import GitHubIcon from '@mui/icons-material/GitHub';
import LaunchIcon from '@mui/icons-material/Launch';

const StyledCard = styled(Card)`
  display: flex;
  background-color: transparent;
  color: #ccd6f6;
  margin-bottom: 20px;
  position: relative;
  overflow: visible;

  &:hover .project-description {
    opacity: 1;
    transform: translateY(0);
  }

  &:hover .cover {
    filter: brightness(0.7);
  }
`;

const ProjectDetails = styled(Box)`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  padding: 20px;
`;

const ProjectDescription = styled(Box)`
  position: absolute;
  top: 20px;
  left: 20px;
  right: 20px;
  background-color: rgba(10, 25, 47, 0.85);
  color: #ccd6f6;
  padding: 20px;
  border-radius: 4px;
  opacity: 0;
  transform: translateY(20px);
  transition: all 0.3s ease-in-out;
`;

const TechList = styled.ul`
  list-style: none;
  padding: 0;
  display: flex;
  flex-wrap: wrap;
  margin-top: 10px;

  li {
    font-size: 12px;
    margin-right: 15px;
    color: #8892b0;
  }
`;

const ProjectLinks = styled(Box)`
  display: flex;
  align-items: center;

  a {
    color: #ccd6f6;
    margin-right: 10px;

    &:hover {
      color: #64ffda;
    }
  }
`;

const ProjectCard = ({ project }) => {
  return (
    <StyledCard>
      <CardMedia
        component="img"
        alt={project.title}
        height="250"
        image={project.cover}
        title={project.title}
        className="cover"
        sx={{ width: '50%' }}
      />
      <ProjectDetails>
        <CardContent>
          <Typography variant="overline" color="#64ffda">
            Featured Project
          </Typography>
          <Typography variant="h5" gutterBottom>
            {project.title}
          </Typography>
          <ProjectDescription className="project-description">
            <Typography variant="body1" paragraph>
              {project.description}
            </Typography>
            <TechList>
              {project.tech.map((tech, index) => (
                <li key={index}>{tech}</li>
              ))}
            </TechList>
          </ProjectDescription>
        </CardContent>
        <CardActions>
          <ProjectLinks>
            {project.github && (
              <a href={project.github} target="_blank" rel="noopener noreferrer">
                <GitHubIcon />
              </a>
            )}
            {project.external && (
              <a href={project.external} target="_blank" rel="noopener noreferrer">
                <LaunchIcon />
              </a>
            )}
          </ProjectLinks>
        </CardActions>
      </ProjectDetails>
    </StyledCard>
  );
};

export default ProjectCard;
