import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Typography, Button, Box } from '@mui/material';
import styled from 'styled-components';

const StyledAppBar = styled(AppBar)`
  background-color: #0a192f !important;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  z-index: 1100;
  transition: background-color 0.3s ease-in-out;
`;

const StyledToolbar = styled(Toolbar)`
  display: flex;
  justify-content: space-between;
`;

const StyledBox = styled(Box)`
  display: flex;
  gap: 1rem;
`;

const Header = () => {
  const [opacity, setOpacity] = useState(1);

  const handleScroll = () => {
    const scrollTop = window.scrollY;
    const newOpacity = scrollTop > 50 ? 0.8 : 1; // Adjust the threshold as needed
    setOpacity(newOpacity);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <StyledAppBar position="fixed" style={{ backgroundColor: `rgba(10, 25, 47, ${opacity})` }}>
      <StyledToolbar>
        <Typography variant="h6" sx={{ color: '#00BFFF' }}>
          My Portfolio
        </Typography>
        <StyledBox>
          <Button color="inherit" href="#about">About</Button>
          <Button color="inherit" href="#work-experience">Work Experience</Button>
          <Button color="inherit" href="#projects">Projects</Button>
          <Button color="inherit" href="#contact">Contact</Button>
        </StyledBox>
      </StyledToolbar>
    </StyledAppBar>
  );
};

export default Header;
