import React from 'react';
import styled from 'styled-components';
import GitHubIcon from '@mui/icons-material/GitHub';
import LaunchIcon from '@mui/icons-material/Launch';

const StyledCard = styled.div`
  display: flex;
  height: 400px;
  background-color: transparent;
  color: #ccd6f6;
  margin-bottom: 20px;
  border-radius: 8px;
  transition: all 0.3s ease-in-out;
  position: relative;

  &:hover .project-details {
    opacity: 1;
    transform: translateY(0);
  }

  @media (max-width: 1000px) {
    flex-direction: column;
    height: auto;
  }
`;

const CoverImage = styled.img`
  width: 50%;
  height: auto;
  filter: brightness(0.7);
  overflow: hidden;

  &:hover {
    filter: brightness(1);
  }

  @media (max-width: 1000px) {
    width: 100%;
  }
`;

const ProjectDetails = styled.div`
  display: flex;
  width: 50%;
  background-color: transparent;
  flex-direction: column;
  justify-content: space-between;
  padding: 20px;
  border-radius: 0 8px 8px 0;

  @media (max-width: 1000px) {
    width: 100%;
  }
`;

const ProjectDetailsTitle = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  height: 50px;
`;

const ProjectDetailsDescription = styled.div`
  display: flex;
  background-color: #282c34;
  color: black;
  padding: 20px;
  border-radius: 4px;
`;



const ProjectDetailsStack = styled.ul`
  list-style: none;
  padding: 0;
  display: flex;
  flex-wrap: wrap;
  margin-top: 10px;
  color: white;
  margin-left: auto;

  li {
    font-size: 14px;
    margin-right: 15px;
    color: #8892b0;
  }
`;

const ProjectLinks = styled.div`
  display: flex;
  align-items: center;
  margin-left: auto;

  a {
    color: #ccd6f6;
    margin-right: 10px;
    display: flex;
    align-items: center;

    &:hover {
      color: #64ffda;
    }

    svg {
      font-size: 1.5rem;
    }
  }
`;




const ProjectCard = ({ project }) => {
  return (

    <StyledCard>
      <CoverImage
        src={project.cover}
        alt={project.title}
        className="cover"
      />

      <ProjectDetails>

        <ProjectDetailsTitle>

          <Typography variant="overline" style={{ color: '#ffefd6' }}>
            Featured Project
          </Typography>
          <Typography variant="h5" style={{ marginBottom: '10px' }}>
            {project.title}
          </Typography>
        </ProjectDetailsTitle>

        <ProjectDetailsDescription>
          <Typography variant="body1" style={{ marginBottom: '20px' }}>
            {project.description}
          </Typography>
        </ProjectDetailsDescription>

        <ProjectDetailsStack>
          {project.tech.map((tech, index) => (
            <li key={index}>{tech}x</li>
          ))}
        </ProjectDetailsStack>

        <ProjectLinks>
          {project.github && (
            <a href={project.github} target="_blank" rel="noopener noreferrer">
              <GitHubIcon />
            </a>
          )}
          {project.external && (
            <a href={project.external} target="_blank" rel="noopener noreferrer">
              <LaunchIcon />
            </a>
          )}
        </ProjectLinks>

      </ProjectDetails>

    </StyledCard>
  );
};

const Typography = styled.p`
  margin: 0;
  color: ${({ variant }) => (variant === 'overline' ? '#f6f7f8' : '#f6f7f8')};
  font-size: ${({ variant }) => (variant === 'h5' ? '1.5rem' : variant === 'body1' ? '1rem' : '0.875rem')};
  text-transform: ${({ variant }) => (variant === 'overline' ? 'uppercase' : 'none')};
  font-weight: ${({ variant }) => (variant === 'h5' ? '600' : '400')};
  margin-bottom: ${({ variant }) => (variant === 'body1' ? '1rem' : '0')};
`;

export default ProjectCard;
