import React from 'react';
import styled from 'styled-components';
import GitHubIcon from '@mui/icons-material/GitHub';
import LaunchIcon from '@mui/icons-material/Launch';

const StyledCard = styled.div`
  display: flex;
  background-color: #112240;
  color: #ccd6f6;
  margin-bottom: 20px;
  position: relative;
  overflow: visible;
  border-radius: 8px;
  box-shadow: 0 10px 30px -15px rgba(2, 12, 27, 0.7);
  transition: all 0.3s ease-in-out;

  &:hover .project-description {
    opacity: 1;
    transform: translateY(0);
  }

  &:hover .cover {
    filter: brightness(0.7);
  }
`;

const CoverImage = styled.img`
  width: 50%;
  height: auto;
  border-radius: 8px 0 0 8px;
`;

const ProjectDetails = styled.div`
  background-color: #112240;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  padding: 20px;
  border-radius: 0 8px 8px 0;
  width: 100%;
`;

const ProjectDescription = styled.div`
  position: absolute;
  top: 20px;
  left: 20px;
  right: 20px;
  background-color: rgba(10, 25, 47, 0.85);
  color: #ccd6f6;
  padding: 20px;
  border-radius: 4px;
  opacity: 0;
  transform: translateY(20px);
  transition: all 0.3s ease-in-out;
`;

const TechList = styled.ul`
  list-style: none;
  padding: 0;
  display: flex;
  flex-wrap: wrap;
  margin-top: 10px;

  li {
    font-size: 12px;
    margin-right: 15px;
    color: #8892b0;
  }
`;

const ProjectLinks = styled.div`
  display: flex;
  align-items: center;

  a {
    color: #ccd6f6;
    margin-right: 10px;
    display: flex;
    align-items: center;

    &:hover {
      color: #64ffda;
    }

    svg {
      font-size: 1.5rem;
    }
  }
`;

const ProjectCard = ({ project }) => {
  return (
    <StyledCard>
      <CoverImage
        src={project.cover}
        alt={project.title}
        className="cover"
      />
      <ProjectDetails>
        <div>
          <Typography variant="overline" style={{ color: '#64ffda' }}>
            Featured Project
          </Typography>
          <Typography variant="h5" style={{ marginBottom: '10px' }}>
            {project.title}
          </Typography>
          <Typography variant="body1" style={{ marginBottom: '20px' }}>
            {project.description}
          </Typography>
          <TechList>
            {project.tech.map((tech, index) => (
              <li key={index}>{tech}</li>
            ))}
          </TechList>
        </div>
        <ProjectLinks>
          {project.github && (
            <a href={project.github} target="_blank" rel="noopener noreferrer">
              <GitHubIcon />
            </a>
          )}
          {project.external && (
            <a href={project.external} target="_blank" rel="noopener noreferrer">
              <LaunchIcon />
            </a>
          )}
        </ProjectLinks>
      </ProjectDetails>
      <ProjectDescription className="project-description">
        <Typography variant="body1" paragraph>
          {project.description}
        </Typography>
        <TechList>
          {project.tech.map((tech, index) => (
            <li key={index}>{tech}</li>
          ))}
        </TechList>
      </ProjectDescription>
    </StyledCard>
  );
};

const Typography = styled.p`
  margin: 0;
  color: ${({ variant }) => (variant === 'overline' ? '#64ffda' : '#ccd6f6')};
  font-size: ${({ variant }) => (variant === 'h5' ? '1.5rem' : variant === 'body1' ? '1rem' : '0.875rem')};
  text-transform: ${({ variant }) => (variant === 'overline' ? 'uppercase' : 'none')};
  font-weight: ${({ variant }) => (variant === 'h5' ? '600' : '400')};
  margin-bottom: ${({ variant }) => (variant === 'body1' ? '1rem' : '0')};
`;

export default ProjectCard;
