import React from 'react';
import { Card, CardMedia, CardContent, Typography, Box, CardActions, Button } from '@mui/material';

const ProjectCard = ({ project }) => {
  return (
    <Card sx={{ maxWidth: 345, position: 'relative', '&:hover .project-overlay': { opacity: 1 } }}>
      <CardMedia
        component="img"
        alt={project.title}
        height="140"
        image={project.cover}
        title={project.title}
      />
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
          {project.title}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {project.tech.join(', ')}
        </Typography>
      </CardContent>
      <Box
        className="project-overlay"
        sx={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          bgcolor: 'rgba(0, 0, 0, 0.7)',
          color: 'white',
          opacity: 0,
          transition: 'opacity 0.3s',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <Typography variant="body1" align="center" paragraph>
          {project.description}
        </Typography>
        <CardActions>
          <Button size="small" color="primary" href={project.github} target="_blank">
            GitHub
          </Button>
        </CardActions>
      </Box>
    </Card>
  );
};

export default ProjectCard;
